import React, { PureComponent, Fragment } from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import i18n from '@dhis2/d2-i18n';
import LayerLegend from '../layers/legend/Legend';

const styles = theme => ({
    root: {
        position: 'absolute',
        right: 10,
        top: 74,
        zIndex: 999,
        boxShadow: '0 1px 5px rgba(0, 0, 0, 0.65)',
        backgroundColor: '#fff',
        fontSize: 14,
    },
    toggle: {
        width: 26,
        height: 26,
        backgroundPosition: '50% 50%',
        backgroundRepeat: 'no-repeat',
        backgroundSize: '16px 16px',
        backgroundImage:
            'url("data:image/png;base64,")',
    },
    content: {
        overflowY: 'auto',
        minWidth: 150,
        maxWidth: 200,
        minHeight: 80,
        maxHeight: 260,
        padding: '6px 10px 6px 6px',
        color: '#333',
    },
    title: {
        margin: 0,
        fontSize: 14,
        lineHeight: '18px',
        paddingBottom: theme.spacing.unit,
    },
    period: {
        display: 'block',
        fontWeight: 'normal',
    },
    alert: {
        paddingBottom: 8,
    },
});

class Legend extends PureComponent {
    static propTypes = {
        layers: PropTypes.array.isRequired,
        classes: PropTypes.object.isRequired,
    };

    state = {
        isOpen: false,
    };

    render() {
        const { layers, classes } = this.props;
        const { isOpen } = this.state;
        const legendLayers = layers
            .filter(layer => layer.legend || layer.alerts)
            .reverse(); // Show top layer first

        return (
            <div className={`dhis2-map-legend ${classes.root}`}>
                {isOpen ? (
                    <div
                        className={classes.content}
                        onMouseLeave={this.onMouseLeave}
                    >
                        {legendLayers.map(this.getLayerLegend)}
                    </div>
                ) : (
                    <div
                        className={classes.toggle}
                        title={i18n.t('Legend')}
                        onMouseEnter={this.onMouseEnter}
                    />
                )}
            </div>
        );
    }

    getLayerLegend = mapView => {
        const { id, layer, legend, serverCluster, data, alerts = [] } = mapView;
        const { classes } = this.props;
        const hasData =
            (Array.isArray(data) && data.length > 0) ||
            serverCluster ||
            layer === 'earthEngine';

        return (
            <div key={id}>
                {legend && (
                    <Fragment>
                        <h2 className={classes.title}>
                            {legend.title}{' '}
                            <span className={classes.period}>
                                {legend.period}
                            </span>
                        </h2>
                        {hasData && <LayerLegend {...legend} />}
                    </Fragment>
                )}
                {alerts.map(alert => (
                    <div key={alert.id} className={classes.alert}>
                        {alert.description}
                    </div>
                ))}
            </div>
        );
    };

    onMouseEnter = () => this.setState({ isOpen: true });

    onMouseLeave = () => this.setState({ isOpen: false });
}

export default withStyles(styles)(Legend);
